import React from "react";


class HelloApp extends React.Component {
    constructor(props) {
    super(props)

    this.state = {
    	counter: 0
    }

    this.handleClick = this.handleClick.bind(this)
	}

	handleClick() {
	  this.setState({
		  counter: this.state.counter + 1
	  })
	}
  render() {
    return <Welcome name="Sara" />
  }
}

export default HelloApp;
